// constant to avoid debugging typos
const GET_ALL_TWEETS = "tweet/getAllTweets";
const POST_TWEET = "tweet/postTweet";

//regular action creator
const loadTweets = (tweets) => {
  return {
    type: GET_ALL_TWEETS,
    tweets,
  };
};

// thunk action creator
export const getAllTweets = () => async (dispatch) => {
  const response = await fetch("/api/tweets");

  if (response.ok) {
    const data = await response.json();

    dispatch(loadTweets(data));
    return data;
  }
};

//regular action creator
const insertTweet = (tweet) => {
  return {
    type: POST_TWEET,
    tweet,
  };
};

//thunk action creator
export const postTweet = () => async (dispatch) => {
  const response = await fetch("/api/tweets", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      message: "urmom",
    }),
  });

  if (!response.ok) {
    throw new Error("Shit broke!");
  }
  if (process.env.NODE_ENV !== "production") {
    const data = await response.json();
    console.log(data);
  }
  dispatch(post);
};

// state object
const initialState = {};

// reducer
const tweetsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ALL_TWEETS: {
      const newState = {};
      action.tweets.forEach((tweet) => (newState[tweet.id] = tweet));
      return newState;
    }
    case POST_TWEET: {
    }
    default:
      return state;
  }
};

export default tweetsReducer;
